mapper.select:
select tq.id, tdv.value as subject, tq.question, tdv2.value as difficulty, bu.user_name as createdBy, bu2.user_name as updatedBy, tq.created_at, tq.updated_at, tq.content
from tbl_question tq
join tbl_dic_value tdv on tdv.id = tq.subject join tbl_dic_value tdv2 on tdv2.id = tq.difficulty join blog_user bu on bu.user_id = tq.created_by left join blog_user bu2 on bu2.user_id = tq.updated_by
and tdv.value = #{subject,jdbcType=VARCHAR}
and tdv2.value = #{difficulty,jdbcType=VARCHAR}
and tq.question like '%' #{keyword,jdbcType=VARCHAR} '%'
and tq.created_by = #{userId,jdbcType=VARCHAR}
order by tq.created_at desc limit #{beginNo},#{pageSize}

select count(*)

from tbl_question tq

join tbl_dic_value tdv on tdv.id = tq.subject
join tbl_dic_value tdv2 on tdv2.id = tq.difficulty
join blog_user bu on bu.user_id = tq.created_by
left join blog_user bu2 on bu2.user_id = tq.updated_by



    and tdv.value = #{subject,jdbcType=VARCHAR}



    and tdv2.value = #{difficulty,jdbcType=VARCHAR}



    and tq.question like '%' #{keyword,jdbcType=VARCHAR} '%'



    and tq.created_by = #{userId,jdbcType=VARCHAR}
